package PSA_;
//Auto UpCasting
public class DataType1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10; //4 bytes
		long y = x; //8 bytes
		System.out.println(y);
		
		float a = 10.3f; //4 bytes
		double b = a; // 8 bytes
		System.out.println(b);


	}

}
