package PSA_;

// Explicit downCasting
public class DataType2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float x = 10.3f; // 4 bytes
		long y = (long)x; 
		System.out.println(y);
		
		long a = 10;
		int b = (int)a;
		System.out.println(b);
		
		int s = 500;
		byte k = (byte)s;
		System.out.println(k);



	}

}
