package PSA_;

public class ElseIF1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=5;
		if(x==0) {
			System.out.println(0);
		}
		else if(x==1) {
			System.out.println(1);
		}
		else if(x==2) {
			System.out.println(2);
		}
		else if(x==3) {
			System.out.println(3);
		}
		else {
			System.out.println("No match found...");
		}

	}

}
