package PSA_;

public class WrapperClass1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String x = "100";
		Integer y = new Integer(x);
		System.out.println(y);
		
		//int
		String x1 = "1";
		int y1 = Integer.parseInt(x1);
		System.out.println(y1);
		
		//byte
		String x2 = "2";
		byte y2 = Byte.parseByte(x2);
		System.out.println(y2);

		//Short
		String x3 = "3";
		short y3 = Short.parseShort(x3);
		System.out.println(y3);
		
		//long
		String x4 = "4";
		short y4 = Short.parseShort(x4);
		System.out.println(y4);
		
		
		//float
		String x5 = "5";
		float y5 = Float.parseFloat(x5);
		System.out.println(y5);
		
		//Double
		String x6 = "6";
		double y6 = Double.parseDouble(x6);
		System.out.println(y6);
		
		//boolean
		String x7 = "7";
		boolean y7 = Boolean.parseBoolean(x7);
		System.out.println(y7);
		

	}

}
